ValidationRule.errorConditionFormula:
OR(
AND( ISCHANGED(Opportunity__c), OR( ($Profile.Name <> $Label.Data_Loader_Profile_Name), ($Profile.Name <> $Label.System_Admin_Profile), ( $Profile.Name <> $Label.Integration_Profile ) ) ), ( AND( ISNEW(), ISCHANGED(Opportunity__c), OR( ($Profile.Name <> $Label.Data_Loader_Profile_Name), ($Profile.Name <> $Label.System_Admin_Profile), ( $Profile.Name <> $Label.Integration_Profile ) ) ) )
)
